mapper:

    SELECT *
    FROM emotion_records
    WHERE user_id = #{userId}



    INSERT INTO emotion_records (emotion_record_id, user_id, emotion_detail_id, diary_entry, created_time, updated_time)
    VALUES (#{emotionRecordId}, #{userId}, #{emotionDetailId}, #{diaryEntry}, #{createdTime}, #{updatedTime})
;


    UPDATE emotion_records
    SET user_id = #{userId},
        emotion_detail_id = #{emotionDetailId},
        diary_entry = #{diaryEntry},
        created_time = #{createdTime},
        updated_time = #{updatedTime}
    WHERE emotion_record_id = #{emotionRecordId};
