mapper.select:
SELECT AVG(record.value)
FROM emotion_record record
WHERE record.user_id = #{userID}

	AND record.created_time BETWEEN #{startDate} AND #{endDate}

SELECT AVG(record.value)
FROM emotion_record record
WHERE record.user_id = #{userID}

	AND record.created_time BETWEEN #{startDate} AND #{endDate}

SELECT AVG(record.value)
FROM emotion_record record
WHERE record.user_id = #{userID}

	AND record.created_time BETWEEN #{startDate} AND #{endDate}

mapper.update:
UPDATE emotion_statics
SET avg_level_monthly = #{avgLevelWeekly}
WHERE user_id = #{userId}

UPDATE emotion_statics
SET avg_level_monthly = #{avgLevelMonthly}
WHERE user_id = #{userId}

UPDATE emotion_statics
SET avg_level_yearly = #{avgLevelYearly}
WHERE user_id = #{userId}
